.bouncing-dots { 
  display: flex; 
  justify-content: space-between; 
  width: 50px; 
  margin: auto; 
} 
.dot { 
  width: 10px; 
  height: 10px; 
  border-radius: 50%; 
  background-color: #3498db; 
  animation: bounce 0.6s infinite alternate; 
} 
.dot:nth-child(1) { 
  animation-delay: 0s; 
} 
.dot:nth-child(2) { 
  animation-delay: 0.2s; 
} 
.dot:nth-child(3) { 
  animation-delay: 0.4s; 
} 
@keyframes bounce { 
  0% { transform: translateY(0); } 
  100% { transform: translateY(-20px); } 
}

.three-dots { 
  display: flex; 
  justify-content: space-between; 
  width: 60px; 
  margin: auto; 
} 
.dot { 
  width: 12px; 
  height: 12px; 
  border-radius: 50%; 
  background-color: #3498db; 
  animation: pulse 1.2s infinite ease-in-out; 
} 
.dot:nth-child(1) { 
  animation-delay: 0s; 
} 
.dot:nth-child(2) { 
  animation-delay: 0.4s; 
} 
.dot:nth-child(3) { 
  animation-delay: 0.8s; 
} 
@keyframes pulse { 
  0%, 100% { transform: scale(1); } 
  50% { transform: scale(1.4); } 
}

@keyframes wave { 
  0%, 100% { transform: translateY(0); } 
  50% { transform: translateY(-20px); } 
} 
.wave-loading { 
  display: flex; 
  justify-content: space-between; 
  width: 80px; 
  height: 20px; 
} 
.block { 
  width: 20px; 
  height: 20px; 
  background-color: #3498db; 
  animation: wave 1s ease-in-out infinite; 
} 
.block:nth-child(1) { 
  animation-delay: 0s; 
} 
.block:nth-child(2) { 
  animation-delay: 0.2s; 
} 
.block:nth-child(3) { 
  animation-delay: 0.4s; 
}

@keyframes bounceBall { 
  0%, 20%, 50%, 80%, 100% { transform: translateY(0); } 
  40% { transform: translateY(-30px); } 
  60% { transform: translateY(-15px); } 
} 
.bouncing-ball { 
  width: 40px; 
  height: 40px; 
  display: flex; 
  justify-content: center; 
  align-items: center; 
  position: relative; 
} 
.ball { 
  width: 20px; 
  height: 20px; 
  border-radius: 50%; 
  background-color: #3498db; 
  animation: bounceBall 1.5s ease-in-out infinite; 
}

.animation-conicGradientLoader { 
  width: 50px; 
  padding: 8px; 
  aspect-ratio: 1; 
  border-radius: 50%; 
  background: #3498db; 
  --_m: conic-gradient(#0000 10%,#000), linear-gradient(#000 0 0) content-box; 
  -webkit-mask: var(--_m); 
  mask: var(--_m); 
  -webkit-mask-composite: source-out; 
  mask-composite: subtract; 
  animation: l3 1s infinite linear; 
} 
@keyframes l3 { 
  to { transform: rotate(1turn); } 
}

.animation-progressLoader3 { 
  width: 120px; 
  height: 10px; 
  border-radius: 8px; 
  background: #1f77b4; 
  position: relative; 
  overflow: hidden; 
} 
.animation-progressLoader3::before { 
  content: ''; 
  position: absolute; 
  top: 0; 
  left: -100%; 
  width: 100%; 
  height: 100%; 
  background-color: #1565c0; 
  animation: progress3 2s infinite linear; 
} 
@keyframes progress3 { 
  0% { left: -100%; } 
  100% { left: 100%; } 
}

