.checkbox-container, .radio-container {
  margin: 20px;
}

.styled-checkbox, .styled-radio {
  display: flex;
  align-items: center;
  font-size: 18px;
  color: #333;
  position: relative;
  cursor: pointer;
  margin-bottom: 15px;
}

.styled-checkbox input, .styled-radio input {
  display: none;
}

.checkbox-custom, .radio-custom {
  width: 20px;
  height: 20px;
  border: 2px solid #3498db;
  border-radius: 5px;
  margin-right: 10px;
  position: relative;
  transition: all 0.3s ease;
}

.checkbox-custom:before, .radio-custom:before {
  content: '';
  position: absolute;
  top: 2px;
  left: 2px;
  width: 14px;
  height: 14px;
  background-color: #3498db;
  border-radius: 3px;
  opacity: 0;
  transition: all 0.3s ease;
}

.styled-checkbox input:checked + .checkbox-custom:before {
  opacity: 1;
}

.styled-radio input:checked + .radio-custom:before {
  width: 10px;
  height: 10px;
  border-radius: 50%;
  top: 5px;
  left: 5px;
}

.styled-checkbox:hover .checkbox-custom, .styled-radio:hover .radio-custom {
  border-color: #2980b9;
  transform: scale(1.1);
}

.styled-checkbox input:checked + .checkbox-custom {
  background-color: #3498db;
  border-color: #3498db;
}

.styled-radio input:checked + .radio-custom {
  background-color: #3498db;
  border-color: #3498db;
}

.styled-radio input:checked + .radio-custom {
  background-color: #f39c12;
  border-color: #f39c12;
}

.styled-checkbox:active .checkbox-custom, .styled-radio:active .radio-custom {
  transform: scale(0.95);
}

.styled-checkbox input:disabled + .checkbox-custom, .styled-radio input:disabled + .radio-custom {
  background-color: #ddd;
  border-color: #ccc;
}

.styled-checkbox input:disabled + .checkbox-custom:before, .styled-radio input:disabled + .radio-custom:before {
  background-color: #ccc;
  opacity: 0.6;
}

.styled-radio input:checked + .radio-custom {
  background-color: #f39c12;
}

