function createStars() {
  const container = document.querySelector('.star-container');

  // Create shimmering stars
  for (let i = 0; i < 100; i++) {
    const star = document.createElement('div');
    const randomLeft = Math.random() * 100;
    const randomTop = Math.random() * 100;
    const randomSize = Math.random() * 2 + 1;
    const randomDelay = Math.random() * 3; // Random delay between 0 and 3 seconds

    star.classList.add('star');
    star.style.left = `${randomLeft}vw`;
    star.style.top = `${randomTop}vh`;
    star.style.width = `${randomSize}px`;
    star.style.height = `${randomSize}px`;
    star.style.animationDelay = `${randomDelay}s`;

    container.appendChild(star);
  }

  // Create frequent shooting stars with random directions and trails
  setInterval(() => {
    const shootingStar = document.createElement('div');
    const randomLeft = Math.random() * 100;
    const randomTop = Math.random() * 100;
    const randomXDirection = (Math.random() - 0.5) * 200 + 'vw'; // Random horizontal direction
    const randomYDirection = (Math.random() - 0.5) * 200 + 'vh'; // Random vertical direction
    const randomDuration = Math.random() * 2 + 1;

    shootingStar.classList.add('shooting-star');
    shootingStar.style.left = `${randomLeft}vw`;
    shootingStar.style.top = `${randomTop}vh`;
    shootingStar.style.setProperty('--x-direction', randomXDirection);
    shootingStar.style.setProperty('--y-direction', randomYDirection);
    shootingStar.style.animationDuration = `${randomDuration}s`;

    container.appendChild(shootingStar);

    // Remove shooting star after animation ends
    setTimeout(() => shootingStar.remove(), randomDuration * 1000);
  }, 1000); // Increased frequency of shooting stars
}

createStars();

